App.directive("treeData",[function(){return{restrict:"ACEM",template:'<ul><li ng-repeat="x in data"><a ui-sref="{{x.Url}}" ng-click="toggleItem(x,$event)"><i class="{{x.Icon||\'fa fa-star\'}}"></i>{{x.Name}}</a><ul ng-if="x.toggle"><li ng-repeat="y in x.submenus" ng-class="y.Level"><a ui-sref="{{y.Url}}"><i class="{{y.Icon||\'fa fa-star\'}}"></i>{{y.Name}}</a></li></ul></li></ul>',replace:!0,scope:{data:"=",toggleItem:"@"},link:function(a,b,c){angular.forEach(a.data,function(a,b){a.submenus&&a.submenus.length>0&&(a.toggle=!1)}),a.toggleItem=function(a,b){a.submenus&&a.submenus.length>0&&(b.preventDefault(),a.toggle=!a.toggle)}}}}]);